import Axios from 'axios' ;
import './Suche.css';
import {Link} from 'react-router-dom';
import axios from 'axios';
import { response } from 'express';

function Suche (){

    const [stoff, setStoff]=useState('');
    const [ergebnis, setErrgebnis] = useState([]);

    const suchFun=()=>{
        axios.post("http://localhost:3001/api/suchSend",{stoff: stoff});
    }
    const resul =()=>{
        axios.get("http://localhost:3001/api/suchEmpfang").then((response)=>{
            setErrgebnis(response.data);
        });
    }

    return (
      <section>
    <label> Suche: </label>
     <input type="text" name="SuchBegriff" className="Suchbegriff" onChange={(e)=>{
       setStoff(e.target.value)
     } }/>
    <button className="SuchKlick" onClick ={ suchFun }>Suche </button>

      </section>
        );
}
export default Suche;
