import React,{ useEffect, useState } from 'react';
import Axios from 'axios' ;
import './Hilfsstoffe.css';
import {Link} from 'react-router-dom';

function Hilfsstoffe (){
    const [hilfsstoffe, setHilfsstoffe] = useState([]);
    useEffect(()=>{
        Axios.get("http://localhost:3001/api/hilfsstoffe").then((response)=>{
            setHilfsstoffe(response.data)
        }); 
      },[]);

    const artHilfsstoffe =(stoffName)=>{
        Axios.post(`http://localhost:3001/api/artHS/${stoffName}`)
      } ;



    return (
       <section className="GesamtList">
        {hilfsstoffe.map((val)=>{
            return ( <section> <section className="EinzelArt">
                    <h1 className="ArtName"> {val.artName} </h1> 
                {()=>artHilfsstoffe(val.artName)
                }
              <p className="beschreibung"> {val.beschreibung} </p>
                  </section> 
                  <section>
                  <hr className="Seperator" />
                      </section>
                      </section>);

            })
            };
         </section> );
}
export default Hilfsstoffe;
