const[hilfsstoff,setHilfsstoff]=useState("");
const[wirkstoff,setWirkstoff]=useState("");

const prufDaten =() =>{
    Axios.post("http://localhost:3001/api/prufen",{wirkstoff:wirkstoff,hilfsstoff:hilfsstoff})
    };
    setHilfsstoff("");
    setWirkstoff("");



    return (
        <section className ="Prufungsformat">
         <label className="StoffLabel">Wirkstoff:</label>
         <input type="text" name="Wirkstoff" className="Stoffinput" 
         onChange={(e)=>{setWirkstoff(e.target.value)}} required></input>
         <label className="StoffLabel">Hilfsstoffe:</label>
         <input type="text" name="Hilfsstoff" className="Stoffinput"
         onChange={(e)=>{setHilfsstoff(e.target.value)}} required></input>
         <Link to="/Pergebnis"> 
            <button className="PrufButton" onClick={prufDaten}
            >Pr√ºfen</button> </Link>
        </section>

         ); 
}
export default Prufen;
